import { useState } from "react"
const task =[
  {
		id:1,
		text: 'Doctors Appointment',
		day: 'Feb 5th at 2:00pm',
		reminder: true,
	},  
	{
		id: 2,
		text: 'Meeting at school',
		day: 'Feb 5th at 3:00pm',
		reminder: true,
	},
	{
		id: 3,
		text: 'Food Shopping',
		day: 'Feb 5th at 1:00pm',
		reminder: false,
	}
]

const Task = () => {
  return (
    <>
		{task.map((task)=>(
			<h3 key={task.id}>{task.text}</h3>
		))}
		</>
  )
}

export default Task
