import {FaTimes} from 'react-icons/fa'
const Task = ({task, onDelete}) => {
  return (
    <div className="task">
      <h3>{task.text} <FaTimes style={{color:'red'}}
        onClick={()=> onDelete}/></h3>
      <p>{task.day}</p>
      <h5>{task.reminder}</h5>
    </div>
  )
}

export default Task
