import {FaTimes} from 'react-icons/fa'
const Task = ({task, onDelete, onToggle}) => {
  return (
    <div className="task" 
      onDoubleClick={() =>onToggle(task.id)}>
      <h3>{task.text} <FaTimes style={{color:'red'}}
        onClick={()=> onDelete(task.id)}/></h3>
      <p>{task.day}</p>
      <h5>{task.reminder}</h5>
    </div>
  )
}

export default Task
